                    <div v-if="item.icon" class="bi me-2 icon-align" width="16" height="16" v-html="displayIcon(item.icon)"></div>
                    <span>{{ item.name }}</span>
                </div>
            </a>
  
          <div v-else>
            <a href="#" 
