               :class="['nav-link', { active: item.isActive }]"
               data-bs-toggle="collapse" 
               :data-bs-target="'#collapse' + index" 
               aria-expanded="false" 
               :aria-controls="'collapse' + index" 
               @click.prevent="handleTabClick(item)" @click="toggleRotation">
